import React from 'react'
import paper from '../images/icon-paper.svg'
import rock from '../images/icon-rock.svg'
import scissors from '../images/icon-scissors.svg'
import {connect} from 'react-redux'
import  '../Style/Gamer.css'
function Gamer({userChoice}) {
  const setUserChoiceStateImage = (choice) =>{
    let image = ""
    switch(choice){
      case 'paper':
        image =  paper
        break;
      case 'rock':
        image = rock
        break;
      case 'scissors':
        image =  scissors
        break;
        default:
          image = ""
    }

    return image

  }

  return (
    <div>
    <div><img className={userChoice} src={setUserChoiceStateImage(userChoice)}/></div> 
    </div>
  )
}

const mapStateToProps = (state) => {
  return {
    userChoice: state.userChoice
  };
};

export default connect(mapStateToProps)(Gamer);
