import React from 'react'
import paper from '../images/icon-paper.svg'
import rock from '../images/icon-rock.svg'
import scissors from '../images/icon-scissors.svg'
import {setUserChoiceStateImage} from '../Shared/SharedFunction'
import {connect} from 'react-redux'
import '../Style/GamerStyle.css'
function Gamer({userChoice}) {
  // const setUserChoiceStateImage = (choice) =>{


  return (
    <div>
    <div><img className={userChoice+"Choice"} src={setUserChoiceStateImage(userChoice)}/></div> 
    </div>
  )
}

const mapStateToProps = (state) => {
  return {
    userChoice: state.userChoice
  };
};

export default connect(mapStateToProps)(Gamer);
