import React, { useState, useEffect }from 'react'
import {setUserChoiceStateImage, getComputerChoice} from '../Shared/SharedFunction'
import {connect} from 'react-redux'
import '../Style/ComputerStyle.css'
function Computer({computerChoice}) {

  const [choice, setChoice] = useState(null)

  useEffect(() =>{

   const timer  = setTimeout(() =>{
      setChoice(getComputerChoice())
      computerChoice(choice)
    }, 1000)
    return () => clearTimeout(timer)
  }, [])


  return (
    <div>
     <div>
      { choice? <img className={choice}  src={setUserChoiceStateImage(choice)}/> :  <div className='null'/>}
     </div> 
    </div>
  )
}

const mapDispatchToProps = (dispatch) => {
  return {
    computerChoice: (choice) => dispatch({ type: "COMPUTER_CHOICE", payload: { choice } }),
  };
};

export default connect(null,mapDispatchToProps)(Computer);
