import React, { useState, useEffect } from "react";
import {
  setUserChoiceStateImage,
  getComputerChoice,
} from "../Shared/SharedFunction";
import { connect } from "react-redux";
import Elements from "../Shared/Elements";
import "../Style/Triangle.css";
function Computer({ computerChoice }) {
  const [choice, setChoice] = useState(null);

  useEffect(() => {
    let state = null;
    setTimeout(() => {
      state = getComputerChoice();
      setChoice(state);
      computerChoice(state);
    }, 1000);
  }, []);

  return (
    <div>
      <div className="computer">
        {choice ? (
          <Elements
            className={choice}
            srcImage={setUserChoiceStateImage(choice)}
          />
        ) : (
          <div className="null"></div>
        )}
      </div>
    </div>
  );
}

const mapDispatchToProps = (dispatch) => {
  return {
    computerChoice: (choice) =>
      dispatch({ type: "COMPUTER_CHOICE", payload: { choice } }),
  };
};

export default connect(null, mapDispatchToProps)(Computer);
