import React, { useState, useEffect } from "react";
import "./App.css";
import Form from './Form'
import { connect } from "react-redux";
import GameDashboard from './GameDashboard'

function App({ user}) {

  if(!user){
    return (
      <div className="App">
        <Form/>
      </div>
    );
  }else{
    return(
      <div className="Game">
        <GameDashboard/>
      </div>
    )
  }

}

