const initialState = {
  user: null,
  score: 0
};

const ReducerApp = (state = initialState, action) =>{
  console.log(action.payload)
  if(action.type === "SET_USER"){
    return{
      ...state,
      user: action.payload.user
    }
  }

  return state;
};

export default ReducerApp;
