import React, { useState, useEffect } from "react";
import ScoreTable from "./ScoreTable";
import Triangle from "./Triangle";
import Rules from "./Rules";
import "../Style/GameStyel.css";

function Game({userChoice}) {
  const [rulesState, setRuleState] = useState(false);


  return (
    <div className="game_dashboard">
      <ScoreTable />
       {userChoice? "": <Triangle />}
      {rulesState ? <Rules /> : ""}
      <div className="rules_button">
        {/* set the rule state from false to true */}
        <button onClick={() =>setRuleState(!rulesState)}>RULES</button>
      </div>
    </div>
  );
}


const mapStateToProps = (state) => {
  return {
    userChoice: state.userChoice
  };
};

export default connect(mapStateToProps)(Game);
