import React from 'react'
import {gameStart} from '../Shared/SharedFunction'
import {connect} from 'react-redux'
import '../Style/WinOrLose.css'
function WinOrLose({userChoice,computerChoice, resetGame}) {


  const showResult = (user, computer) =>{
    if(user && computer){
      return <div className="win_lose">
      <h1 >
        { gameStart(userChoice, computerChoice)? "You win": "You Lose"}
        </h1>
      <button onClick={() => resetGame(null)}>Play Again</button>
    </div>
    } else{
      return;
    }
  }

 return (
   <>
  {showResult(userChoice, computerChoice)}
  </>
  )
}
const mapDispatchToProps = (dispatch) => {
  return {
    resetGame: (choice) => dispatch({ type: "RESET", payload: { choice } }),
  };
}


const mapStateToProps = (state) => {
  return {
    userChoice: state.userChoice,
    computerChoice: state.computerChoice
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(WinOrLose);
