import React from "react";
import Pentagon from "../images/bg-triangle.svg";
import paper from "../images/icon-paper.svg";
import rock from "../images/icon-rock.svg";
import scissors from "../images/icon-scissors.svg";
import { connect } from "react-redux";
import Elements from '../Shared/Elements'
import "../Style/Triangle.css";

function Triangle({ setUserChoice }) {
  return (
    <div>
      <div className="triangle">
        <Elements className={"paper"} srcImage={paper} setUserChoice={setUserChoice}/>
        <div
          className="circle circle--scissors"
          onClick={(e) => setUserChoice(e.target.className)}
        >
          <img className="scissors" src={scissors} />
        </div>
        <div
          className="circle circle--rock"
          onClick={(e) => setUserChoice(e.target.className)}
        >
          <img className="rock" src={rock} />
        </div>
        <img src={Pentagon} />
      </div>
    </div>
  );
}

const mapDispatchToProps = (dispatch) => {
  return {
    setUserChoice: (choice) =>
      dispatch({ type: "SET_USER_CHOICE", payload: { choice } }),
  };
};

export default connect(null, mapDispatchToProps)(Triangle);
