import React, { useState, useEffect }from 'react'
import {setUserChoiceStateImage, getComputerChoice} from '../Shared/SharedFunction'
import {connect} from 'react-redux'
import Elements from '../Shared/Elements'
import '../Style/Elements.css'
function Computer({computerChoice,showResult, changeClass }) {

  let className = "computer"
  let classNameForDashboard = "result-dashboard computer"
  const [choice, setChoice] = useState(null)

  useEffect(() =>{

    let state = null
    setTimeout(() =>{
    state = getComputerChoice()
    setChoice(state)
    computerChoice(state)
    changeClass(true)
    }, 1000)

  }, [])



  return (
    <div>
     <div  className={showResult ? `${classNameForDashboard}` : `${className}`}>
       <h1>THE HOUSE PICKED</h1>
      { choice? <Elements  className={choice} srcImage={setUserChoiceStateImage(choice)}/> :  <div className='null'></div>}
     </div> 
    </div>
  )
}

const mapDispatchToProps = (dispatch) => {
  return {
    computerChoice: (choice) => dispatch({ type: "COMPUTER_CHOICE", payload: { choice } }),
    changeClass: (classSate) => dispatch({type: "CHANGE_CLASS", payload:{classSate}} )
  };
};

const mapStateToProps = (state) =>{
  return{
    showResult: state.showResult,
  }
}

export default connect(mapStateToProps,mapDispatchToProps)(Computer);
