import React, { useState, useEffect } from 'react'
import {gameStart} from '../Shared/SharedFunction'
import {connect} from 'react-redux'
import '../Style/WinOrLose.css'
function WinOrLose({computerChoice, userChoice, resetState, setScore}) {

  const [result, setResult] = useState(null)

  useEffect(() =>{
    let outCome = gameStart(userChoice,computerChoice)
    setResult(outCome)
    setScore(outCome)
  },[])


 return (
  <div className="win_lose">
    <h1>{result==="draw"? result :`YOU ${result}`}</h1>
    <button onClick={() => resetState(null)}>Play Again</button>
  </div>
  )
}


const mapDispatchToProps = (dispatch) => {
  return {
    resetState: (choice) => dispatch({ type: "RESET", payload: { choice } }),
    setScore: (result) => dispatch({ type: "RESULT", payload: { result } }),
  };
}


const mapStateToProps = (state) => {
  return {
    userChoice: state.userChoice,
    computerChoice: state.computerChoice,
    score: state.score
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(WinOrLose);
