import React, { useState, useEffect }from 'react'
import {setUserChoiceStateImage} from '../Shared/SharedFunction'
import {connect} from 'react-redux'
import '../Style/ComputerStyle.css'
function Computer({computerChoice}) {

  const [choice, setChoice] = useState(null)

  useEffect(() =>{
     setChoice(getComputerChoice())
    computerChoice(choice)
  })

  const getComputerChoice = () =>{
    let computerChoice = ["paper","rock","scissors"]
    let random = Math.floor(Math.random() * computerChoice.length)
    return computerChoice[random]
  }
  return (
    <div>
     <div>
       <img className={choice}  src={setUserChoiceStateImage(choice)}/>
       <div className='null'/>
     </div> 
    </div>
  )
}

const mapDispatchToProps = (dispatch) => {
  return {
    computerChoice: (choice) => dispatch({ type: "COMPUTER_CHOICE", payload: { choice } }),
  };
};

export default connect(null,mapDispatchToProps)(Computer);
