import React from 'react'
import paper from '../images/icon-paper.svg'
import rock from '../images/icon-rock.svg'
import scissors from '../images/icon-scissors.svg'
import  '../Style/Triangle.css'

export default function index({userChoice}) {

  const setUserChoiceStateImage = (choice) =>{
    let image = ""
    switch(choice){
      case 'paper':
        image =  paper
        break;
      case 'rock':
        image = rock
        break;
      case 'scissors':
        image =  scissors
        break;
        default:
          image = ""
    }

    return image

  }

  return (
    <div>
    <div></div> <img className={setUserChoiceStateImage(userChoice)} src={setUserChoiceStateImage(userChoice)}/>
    </div>
  )
}
